mapper.insert:
insert into boardpart(
bpno, body, bno, oriname, savename, dir
bpno, body, bno
) values(
(select NVL(MAX(bpno) + 1, 1001) from boardpart), #{body}, #{bno}, #{oriname}, #{savename}, '/fri/img/upload/'
(select NVL(MAX(bpno) + 1, 1001) from boardpart), #{body}, #{bno}
)

insert into
    notice(nno, nbody, ncode, nupno)
values(
    (select nvl(max(nno) + 1, 1001) from notice),
    #{nbody}, 3, #{nupno}
)
